cp "$File" "$File.bak"

$lines = Get-Content "$File.bak"
$lines[0] | Out-File "$File"

for ($i = 1; $i -le $lines.length; $i++) {
    if (-not $lines[$i]) {
        continue
    }

    $key = ($lines[$i] -split "\|")[0]
    $x = $lines[$i] -replace $key, $map[$key]
    $x = $x.Substring(0, $x.LastIndexOf('|'))
    $x | Out-File -Append "$File"
}
